
$(document).ready(function(){$("#gallery-slider #first").load(function(){var c=$("#gallery-slider").height();var b=parseInt($("#gallery-slider").css("border-top-width").replace("px",""));var a=parseInt($("#gallery-slider").css("border-bottom-width").replace("px",""));$("#gallery-slider img").height(c-(b+a))});$("#slider-left").click(function(){var b=$("#gallery-slider").getInterval();var a=0;var d=parseInt($("#first").css("margin-left").replace("px",""));var c=(d+b)+"px";if(d<a){$("#first").animate({"margin-left":c},250,"linear")}$("#first").checkSliderControlButtons(d,a,b)});$("#slider-right").click(function(){var c=$("#gallery-slider").getInterval();var d=$("#gallery-slider img").size();var a=c*d;var b=(a-c)*-1;var f=parseInt($("#first").css("margin-left").replace("px",""));var e=(f-c)+"px";if(f>b){$("#first").animate({"margin-left":e},250,"linear")}$("#first").checkSliderControlButtons(f,b,c)});$.fn.getInterval=function(){return $("#gallery-slider").width()+parseInt($("#gallery-slider").css("border-left").replace("px",""))};$.fn.checkSliderControlButtons=function(c,b,a){if(b===0){if((c+a)<b){$("#slider-right").removeClass("slider-control-disabled")}else{$("#slider-left").addClass("slider-control-disabled")}}else{if((c-a)>b){$("#slider-left").removeClass("slider-control-disabled")}else{$("#slider-right").addClass("slider-control-disabled")}}}});$(document).on("pagehide","#booking-engine",function(){$("#apartments #apartments-button-menu").addClass("ui-btn-active")});